import React, { Component } from 'react'
import './NewsHome.css';
// -- redux
import { connect } from 'react-redux'
import { getNews, toggleLoading } from '../../store/actions/news/news-actions';

import loadingGif from '../../images/loading.gif';
import NewsContainer from '../news-container/NewsContainer';

import { OverlayTrigger, Tooltip, Button, Modal } from 'react-bootstrap';

class NewsHome extends Component {

    constructor(props) {
        super(props)

        this.state = {
            modalShow: false
        }
    }


    componentDidMount() {
        const { id, getNews, toggleLoading } = this.props;
        toggleLoading();
        getNews(id);
    }

    render() {
        const { news, loading } = this.props;
        const { modalShow } = this.state;
        return (
            <div className="news-home">
                <header>
                    <div className="">
                        <h1> Latest News </h1>
                        <span>  </span>
                    </div>
                    <OverlayTrigger
                        placement="bottom"
                        overlay={
                            <Tooltip>
                                New subscription.
                            </Tooltip>
                        }
                    >
                        <i class="fas fa-plus"></i>
                    </OverlayTrigger>
                </header>
                <div className="container">
                    {
                        loading ? <img src={loadingGif} alt="Loading..."/> : <NewsContainer news={news} />
                    }
                </div>
                <Modal show={modalShow} onHide={() => this.setState({ modalShow: false })}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={() => this.setState({ modalShow: false })}>
                            Close
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    id: state.user.data.id,
    news: state.news.news,
    loading: state.news.loading
});

export default connect(mapStateToProps, { getNews, toggleLoading })(NewsHome);
