import React, { Component } from "react";
import Slider from "react-slick";
import Slide from "./slide/Slide";

import a from '../../images/1.jpeg';
import b from '../../images/2.jpeg';
import c from '../../images/3.jpeg';
import d from '../../images/4.jpeg';
import e from '../../images/5.jpeg';



export default class HomepageSlider extends Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 1
    };
    const items = [
        { bkg: a, text: 'tracking news it has never been so easy' },
        { bkg: b, text: 'free to use' },
        { bkg: c, text: 'unlimited subscriptions ' }
    ];
    return (
      <div>
        <Slider {...settings}>
          {
              items.map((item, index) => <Slide slide={item} /> )
          }
        </Slider>
      </div>
    );
  }
}
