import React, { Component } from 'react'
import './Navbar.css';
import ModalComponent from '../modal/Modal';
import Register from '../register-login/register/Register';

export default class Navbar extends Component {
    constructor(props) {
        super(props)

        this.state = {
            registerOpen: false,
            loginOpen: false
        }
    }

    toggleOff = () => {
        this.setState({ 
            registerOpen: false, 
            loginOpen: false,
        });
    }

    render() {
        const { registerOpen, loginOpen } = this.state;
        const wrapper = (<div className={wrapperClass} onClick={() => this.toggleOff()}></div>);
        return (
            <div className="navbar">
                <div className="">
                    <h1> {`<News Tracking/>`} </h1>
                </div>
                <div className="utils">
                    <p onClick={() => this.setState({ registerOpen: true, wrapperClass: "wrapper show" })}> Register </p>
                    <p onClick={() => this.setState({ loginOpen: true, wrapperClass: "wrapper show" })}> Login </p>
                </div>

                <ModalComponent title={"Register"} content={<Register /> } show={registerOpen} onHide={() => this.setState({ registerOpen: false })} />
                <ModalComponent title={"Login"} show={loginOpen} onHide={() => this.setState({ loginOpen: false })} />
            </div>
        );
    }
}
