import React, { Component } from 'react'
import './NewsHome.css';
// -- redux
import { connect } from 'react-redux'
import { getNews, toggleLoading, getSources } from '../../store/actions/news/news-actions';

import loadingGif from '../../images/loading.gif';
import NewsContainer from '../news-container/NewsContainer';

import { OverlayTrigger, Tooltip, Button, Modal } from 'react-bootstrap';

class NewsHome extends Component {

    constructor(props) {
        super(props)

        this.state = {
            modalShow: false,
            newSubscription: ''
        }
    }

    componentDidMount() {
        const { id, getNews, toggleLoading, getSources } = this.props;
        toggleLoading();
        getNews(id);
        getSources();
    }

    addSubscription = () => {
        const subscriptionId = 
        this.setState({ modalShow: false });
    }

    render() {
        const { news, loading, sources } = this.props;
        const { modalShow } = this.state;
        const newsJournalsIDs = news && news.map((item) => item.journalID);
        const modalBody = (
            <div>
                <label htmlFor="browser">Choose a journal :</label>
                <input list="browsers" name="browser" id="browser"  onChange={(e) => this.setState({ newSubscription: e.target.value })} />

                <datalist id="browsers">
                    {
                        sources && sources.map((item) => newsJournalsIDs.includes(item.id) ? null : <option value={item.title} key={item.id} data-key={item.id} onClick={(e) => this.setState({ newSubscription: e.target.getAttribute('data-key') })} /> )
                    }
                </datalist>
            </div>
        );
        return (
            <div className="news-home">
                <header>
                    <div className="">
                        <h1> Latest News </h1>
                        <span>  </span>
                    </div>
                    <OverlayTrigger
                        placement="bottom"
                        overlay={
                            <Tooltip>
                                New subscription.
                            </Tooltip>
                        }
                    >
                        <i onClick={() => this.setState({ modalShow: true })} class="fas fa-plus"></i>
                    </OverlayTrigger>
                </header>
                <div className="container">
                    {
                        loading ? <img src={loadingGif} alt="Loading..."/> : <NewsContainer news={news} />
                    }
                </div>

                <Modal 
                show={modalShow}
                onHide={() => this.setState({ modalShow: false })}
                size="md"
                aria-labelledby="contained-modal-title-vcenter"
                 centered
                >
                    <Modal.Header closeButton>
                        <Modal.Title> New subscription. </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        { modalBody }
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={() => this.setState({ modalShow: false })}>
                            Close
                        </Button>
                        <Button variant="success" onClick={() => this.addSubscription()}>
                            Add
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    id: state.user.data.id,
    news: state.news.news,
    loading: state.news.loading,
    sources: state.news.sources
});

export default connect(mapStateToProps, { getNews, toggleLoading, getSources })(NewsHome);
