import Slide from "./slide/Slide";

import './HomepageSlider.css';

import a from '../../images/1.jpeg';
import b from '../../images/2.jpeg';
import c from '../../images/3.jpeg';
// import d from '../../images/4.jpeg';
// import e from '../../images/5.jpeg';



