      //       <div className="">
      //           <img src={b} alt=""/>
      //       </div>
      //   </Slider>
      // </div>
      <div className="homepage-slider-container">
        <h2> Single Item</h2>
        <Slider {...settings} className="slider">
          <div>
            <h3>1</h3>
            <img src={a} alt=""/>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
        </Slider>
      </div>
    );
  }
}
