import React, { Component } from 'react'
import './Navbar.css';
import ModalComponent from '../modal/Modal';

export default class Navbar extends Component {
    constructor(props) {
        super(props)

        this.state = {
            registerOpen: false,
            loginOpen: false,
            wrapperClass: "wrapper",
            modalClass: "show"
        }
    }

    toggleOff = () => {
        this.setState({ 
            registerOpen: false, 
            loginOpen: false,
        });
    }

    render() {
        const { registerOpen, loginOpen, wrapperClass, modalClass } = this.state;
        const wrapper = (<div className={wrapperClass} onClick={() => this.toggleOff()}></div>);
        return (
            <div className="navbar">
                <div className="">
                    <h1> {`<News Tracking/>`} </h1>
                </div>
                <div className="utils">
                    <p onClick={() => this.setState({ registerOpen: true, wrapperClass: "wrapper show" })}> Register </p>
                    <p onClick={() => this.setState({ loginOpen: true, wrapperClass: "wrapper show" })}> Login </p>
                </div>
                {/* {
                    registerOpen && <Modal className={modalClass} title={'Register'} content={<h1>hello</h1>} />
                }
                {
                    loginOpen && <Modal className={modalClass} title={'Login'} content={<h1>hello</h1>} />
                }
                {
                    (loginOpen || registerOpen) && wrapper
                } */}
                <ModalComponent show={registerOpen} onHide={() => this.setState({ registerOpen: false })} />
                <ModalComponent show={loginOpen} onHide={() => this.setState({ loginOpen: false })} />
            </div>
        );
    }
}
