import React, { Component } from 'react'
import './Navbar.css';
import Modal from '../modal/Modal';

export default class Navbar extends Component {
    constructor(props) {
        super(props)

        this.state = {
            registerOpen: false,
            loginOpen: false,
            wrapperClass: "wrapper"
        }
    }

    toggleOff = () => {
        this.setState({
            wrapperClass: "wrapper hide"
        });
        setTimeout(() => {
            this.setState({ 
                registerOpen: false, 
                loginOpen: false,
            });
        }, 500);
    }

    render() {
        const { registerOpen, loginOpen } = this.state;
        // const wrapperClass = loginOpen || registerOpen ? 
        const wrapper = (<div className="wrapper" onClick={() => this.toggleOff()}></div>);
        return (
            <div className="navbar">
                <div className="">
                    <h1> {`<News Tracking/>`} </h1>
                </div>
                <div className="utils">
                    <p onClick={() => this.setState({ registerOpen: true, wrapperClass: "wrapper show" })}> Register </p>
                    <p onClick={() => this.setState({ loginOpen: true, wrapperClass: "wrapper show" })}> Login </p>
                </div>
                {
                    registerOpen && <Modal title={'Register'} content={<h1>hello</h1>} />
                }
                {
                    loginOpen && <Modal title={'Login'} content={<h1>hello</h1>} />
                }
                {
                    (loginOpen || registerOpen) && wrapper
                }
            </div>
        );
    }
}
