import { GET_NEWS } from './news-types';

import Axios from 'axios';

export const getNews = (id) => async (dispatch) => {
    const userId = id;
    try {
        const { data: { news } } = await Axios.post('/news/get-news', {id: userId});
        // console.log(news);
        return dispatch({
            type: GET_NEWS,
            payload: news
        })
    } catch (error) {
        throw error;   
    }
}
