import React, { Component } from 'react'
import './NewsHome.css';
// -- redux
import { connect } from 'react-redux'
import { getNews, toggleLoading } from '../../store/actions/news/news-actions';

import loadingGif from '../../images/loading.gif';

class NewsHome extends Component {

    componentDidMount() {
        const { id, getNews } = this.props;
        toggleLoading();
        getNews(id);
    }

    render() {
        const { news, loading } = this.props;
        return (
            <div className="news-home">
                <header>
                    <h1> Latest News </h1>
                    <span>  </span>
                </header>
                <div className="container">
                    {
                        loading && <img src={loadingGif} alt="Loading.."/>
                    }
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    id: state.user.data.id,
    news: state.news.news,
    loading: state.news.news
});

export default connect(mapStateToProps, { getNews, toggleLoading })(NewsHome);
