import React, { Component } from 'react'
import './App.css';

import { Route, Switch } from 'react-router-dom';
import Navbar from './components/navbar/Navbar';
import Home from './pages/Home';
//////////
import { connect } from 'react-redux';
import { decodeTheJWT } from './store/actions/user/user-actions';

class App extends Component {

  componentDidMount() {
    const token = localStorage.getItem('token'); 
    if(token) {
      this.props.decodeTheJWT(token);
    }
  }
  render() {
    console.log(this.props.firstName);
    return (
      <>
        <Navbar />
        <Switch>
            { !this.props.firstName && <Route exact path='/' component={Home} />}
            {/* <Route exact path='/' component={Home} /> */}
            {/* <Route path='/products' component={Products} /> */}
            {/* <Route exact path='/stores' component={Stores} /> */}
            {/* {
              this.props.products && this.props.products.map((product) => (
                <Route key={product.code} path={`/${product.category}/${product.code}`} render={(props) => <ProductPage {...props} product={product} />}  />
              ))
            } */}
            {/* { userConnected && <Route path="/profil" component={UserProfile} /> } */}
            {/* { isadmin && <Route path='/admin-panel' component={AdminPanel} /> } */}
        </Switch>
      </>
    )
  }
}

const mapStateToProps = (state) => ({
  firstName: state.user.data.firstName
});

export default connect(mapStateToProps, { decodeTheJWT })(App);
